import React from 'react'
import './App.css';

class App extends React.Component {
    render() {

      let isTestCreated = false
      const createTest = () => {
        if(!isTestCreated) {
          isTestCreated = true
          let val = document.getElementById("testName").value;
          document.getElementById("divTest").style.display="block"
          document.getElementById("test").innerHTML+="" + val
        }
      }

      return (
        <div className="App">
          <h4>Система тестирования</h4>
          <input id="testName" type="text" placeholder="Введите название теста"/>
          <button id="testButton" onClick={createTest}>Создать</button>

          <div id="divTest">
            <h4>Название теста: </h4>
            <nobr><p id="test"></p></nobr>
          </div>

        </div>
      )
    }
}

export default App;
